import React, { useEffect, useState } from 'react';
import './App.css';
import getGifs from './services/getGifs';




function App() {


  const [gifs, setGifs] = useState([]);




  //Se ejecutara cada vez que se renderice un componente
  useEffect(function () {
    //Una vez obtener los gifs actualizar el estado
    getGifs( {keyword: 'rick' }).then(gifs => setGifs(gifs))
  }, [])

  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => {
            return <div>
                <h4>{singleGif.title}</h4>
                <img src={singleGif.url} />
            </div>

          })

        }

      </section>
    </div>
  );
}

export default App;
