import React, { useEffect, useState } from 'react';
import './App.css';
import getGifs from './services/getGifs';
import Gif from './components/Gif'




function App() {


  const [gifs, setGifs] = useState([]);




  //Se ejecutara cada vez que se renderice un componente
  useEffect(function () {
    //Una vez obtener los gifs actualizar el estado
    getGifs( {keyword: 'rick' }).then(gifs => setGifs(gifs))
  }, [])

  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif =>
            <Gif title={singleGif.title} url={singleGif.url} id =
            {singleGif.id} />
          )
        }

      </section>
    </div>
  );
}

export default App;
