import React, {useState} from 'react'
import { Link, useLocation } from 'wouter'
import {useGifs} from '../../hooks/useGifs'

const POPULAR_GIFS = ['Matrix', 'Chile', 'Matias Fernandez', 'Colombia', 'Ecuador', 'Venezuela']

function Home() {

    const [keyword, setKeyword] = useState('')
    const [path, pushLocation] = useLocation()

    const {loading, gifs} = useGifs({ keyword: ''})

    const handleSubmit = evt => {
        
        evt.preventDefault()
        
        //navegar a otra ruta
        pushLocation(`/search/${keyword}`)
    }

    const handleChange = evt => {
        setKeyword(evt.target.value)
    }



    return (
        <>
            <form onSubmit={handleSubmit}>
                <input placeholder="Search a gif here ..." type="text" onChange={handleChange} value={keyword}/>
                <input type="submit" value="Buscar"/>
            </form>
            <h3 className="App-title"> Los Gifs mas populares</h3>
            <ul>
                {POPULAR_GIFS.map((popularGif) => (
                    <li key={popularGif}>
                        <Link 
                            to={`/search/${popularGif}`}>
                        Gifs populares {popularGif}
                        </Link>
                    </li>
                ))}
            </ul>
        </>
    )
}

export default Home
