import React, { useEffect ,useState } from 'react';
import './App.css';


const GIFS = [
  'https://media.giphy.com/media/9SvQFZ5xoGHza/giphy.gif',
  'https://media.giphy.com/media/xUPGcjickCJB2QOcM0/giphy.gif'
]

const DIFFERENT_GIFS = [
  'https://media.giphy.com/media/1gXgsdGRiydyUYGr9Q/giphy.gif'
]

function App() {


  const [gifs , setGifs ] = useState([GIFS]);




  //Se ejecutara cada vez que se renderice un componente
  useEffect(function (){
    console.log('Se esta actualizando el componente');
    setGifs(DIFFERENT_GIFS);
  }, [])

  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => <img src={singleGif} />)
        }

      </section>
    </div>
  );
}

export default App;
